    private static ArrayList<GameItem> getGameItems(Context context)
    {
        ArrayList<GameItem> gameItems = new ArrayList<>(Arrays.asList(originalGameItems));
        SavedValues savedValues= new SavedValues(context);
        for (int i =0 ; i<gameItems.size(); i++) {
            GameItem recordGameItem = savedValues.getGameItem(gameItems.get(i).getId());
            if(recordGameItem!=null){
                gameItems.set(i, recordGameItem);
            }
        }
        return gameItems;
    }

    public static GameItem getGameItem(String Id, Context context)
    {
        ArrayList<GameItem> gameItems = getGameItems(context);
        for(int i = 0; i<gameItems.size(); i++)
        {
            if(gameItems.get(i).getId().equals(Id)) return gameItems.get(i);
        }

        return null;
    }
    public static ArrayList<GameItem> getGameItem(String[] ids, Context context)
    {
        ArrayList<GameItem> gameAllItems = getGameItems(context), gameItems = new ArrayList<>();
        List<String> listGame = Arrays.asList(ids);
        for(int i = 0; i<gameAllItems.size(); i++) {
            if (listGame.contains(gameAllItems.get(i).getId())) {
                gameItems.add(gameAllItems.get(i));
            }
        }

        return gameItems;
    }

