    public static ArrayList<LevelItem> getLevelItems(Context context)
    {
        ArrayList<GameItem> gameAllItems = getGameItems(context),
                size0303 = null, size0404 = null, size0505 = null, size0606 = null, size0707 = null, size0808 = null, size0909 = null;
        for (GameItem gameItem:gameAllItems)
        {
            switch (gameItem.getGroupId())
            {
                case 33:
                    if(size0303 == null) size0303 = new ArrayList<>();
                    size0303.add(gameItem);
                    break;
                case 44:
                    if(size0404 == null) size0404 = new ArrayList<>();
                    size0404.add(gameItem);
                    break;
                case 55:
                    if(size0505 == null) size0505 = new ArrayList<>();
                    size0505.add(gameItem);
                    break;
                case 66:
                    if(size0606 == null) size0606 = new ArrayList<>();
                    size0606.add(gameItem);
                    break;
                case 77:
                    if(size0707 == null) size0707 = new ArrayList<>();
                    size0707.add(gameItem);
                    break;
                case 88:
                    if(size0808 == null) size0808 = new ArrayList<>();
                    size0808.add(gameItem);
                    break;
                case 99:
                    if(size0909 == null) size0909 = new ArrayList<>();
                    size0909.add(gameItem);
                    break;
            }
        }
        ArrayList<LevelItem> levelItems = new ArrayList<LevelItem>();
        levelItems.add(new LevelItem(1, 3, 3, size0303));
       // levelItems.add(new LevelItem(2, 4, 3, 3, new String[]{"0403GAME01", "0403GAME01"}));
        levelItems.add(new LevelItem(3, 4, 4, size0404));
       // levelItems.add(new LevelItem(4, 4, 5, 3, new String[]{"0405GAME01"}));
        levelItems.add(new LevelItem(5, 5, 5, size0505));
        levelItems.add(new LevelItem(6, 6, 6, size0606));
      //  levelItems.add(new LevelItem(7, 7, 6, 3, new String[]{}));
        levelItems.add(new LevelItem(8, 7, 7, size0707));
        levelItems.add(new LevelItem(8, 8, 8, size0808));
        levelItems.add(new LevelItem(8, 9, 9, size0909));

        return levelItems;
    }
